(classic_lasagna_with_meat)-[:INGREDIENT {required_amount_in_grams: 200}]->(bolognese_sause),
(classic_lasagna_with_meat)-[:INGREDIENT {required_amount_in_grams: 15}]->(wheat_flour),
(classic_lasagna_with_meat)-[:INGREDIENT {required_amount_in_grams: 20}]->(butter),
(classic_lasagna_with_meat)-[:INGREDIENT {required_amount_in_grams: 300}]->(milk),
(classic_lasagna_with_meat)-[:INGREDIENT {required_amount_in_grams: 120}]->(readymade_dry_lasagna_sheets),
